    margin: auto;
    background: #BFCFFF;
    margin-top: 20px;

`

export const MidFooterItemWrapper = styled.div`
    position: relative;
    display: inline-block;
    justify-position: center;
    `

export const MidFooterImage = styled.div`
    position: absolute;        

    left: 0; 
    top: 0;
    width: 80px;
    height: 80px;
    border-radius: 40px;
    border: 1px solid black;
