import styled from 'styled-components'

export const HeroContainer = styled.div`
background: white;
display: flex;
justify-content: center;
align-items: center;
padding: 0 30px; 
height: 600px;
position: relative;
z-index: 1;
`;

export const HeroIntro = styled.div`
background: #BFCFFF;
margin: 20px;
margin-top: 60px;
border-radius: 20px;

width: 100%;
height: 100%;
// overflow: hidden;

@media screen and (max-width: 785px){
    margin:0px;
}

`;
