import styled from 'styled-components'

export const HeroContainer = styled.div`
background: white;
display: flex;
justify-content: center;
align-items: center;
padding: 0 30px; 
height: 600px;
position: relative;
z-index: 1;
`;

export const HeroIntro = styled.div`
// will need to set the correct display formatting for this
background: #BFCFFF;
margin: 20px;
margin-top: 60px;
border-radius: 20px;
width: 100%;
height: 100%;
@media screen and (max-width: 785px){
    margin:0px;
}

`;

export const HeroTitle = styled.h1`

`

export const HeroText = styled.p`

`
