import styled from 'styled-components'

export const ContactWrapper = styled.div`
    height: 100px;
    margin-top: 20px;   
    display: block;
    justify-content: center;

`;

export const ContactTitle = styled.h1`
    width:100%;
    text-align: center;

    &:before{
        content: "";
        position: absolute;
        margin-top: 60px;
        width: 20%;
        height: 1px;
        left: 40%;
